import React, { useState } from "react";
import axios from "axios";

import Weather from "./Weather";
import Forecast from "./Forecast";

import "./App.css";

export default function Search() {
  let [city, setCity] = useState("London");
  let [submit, setSubmit] = useState(true);

//for Current Temp
  let [desc, setDesc] = useState(null);
  let [temp, setTemp] = useState(null);
  let [hum, setHum] = useState(null);
  let [wind, setWind] = useState(null);
  let [emoji, setEmoji] = useState(null);

//for forecast

let [temp1 , setTemp1] = useState(null);
let [temp2 , setTemp2] = useState(null);
let [temp3 , setTemp3] = useState(null);
let [temp4 , setTemp4] = useState(null);
let [temp5 , setTemp5] = useState(null);

let [emoji1 , setEmoji1] = useState(null);
let [emoji2 , setEmoji2] = useState(null);
let [emoji3 , setEmoji3] = useState(null);
let [emoji4 , setEmoji4] = useState(null);
let [emoji5 , setEmoji5] = useState(null);

let [date1 , setDate1] = useState(null);
let [date2 , setDate2] = useState(null);
let [date3 , setDate3] = useState(null);
let [date4 , setDate4] = useState(null);
let [date5 , setDate5] = useState(null);

let forecast = [
    {
       date: date1,
       temp:temp1,
       emoji:emoji1

    },
    {
       date: date2,
       temp:temp2,
       emoji:emoji2
    },
    {
        date: date3,
        temp:temp3,
        emoji:emoji3

    },
    {
        date: date4,
        temp:temp4,
        emoji:emoji4
    },
    {
        date: date5,
        temp:temp5,
        emoji:emoji5
    }
]


  function getData(response) {
    setDesc(response.data.weather[0].description);
    setTemp(response.data.main.temp);
    setHum(response.data.main.humidity);
    setWind(response.data.wind.speed);
    setEmoji(response.data.weather[0].icon);
  }

  function getForecast(response){
    console.log(response);
    setTemp1(response.list[0].temp.day);
    setEmoji1(response.list[0].weather[0].icon);
    setDate1(response.list[0].dt);

    setTemp2(response.list[1].temp.day);
    setEmoji2(response.list[1].weather[0].icon);
    setDate2(response.list[1].dt);

    setTemp3(response.list[2].temp.day);
    setEmoji3(response.list[2].weather[0].icon);
    setDate3(response.list[2].dt);

    setTemp4(response.list[3].temp.day);
    setEmoji4(response.list[3].weather[0].icon);
    setDate4(response.list[3].dt);

    setTemp5(response.list[4].temp.day);
    setEmoji5(response.list[4].weather[0].icon);
    setDate5(response.list[4].dt);

  }


  function changeCity(event) {
    event.preventDefault();
    setCity(event.target.value);
    setSubmit(false);
  }


  let Apikey = `3f6be1c407b0d9d1933561808db358ba`;
  let forecastKey = 'b40b135798f82a05aed08769f9275f50';



  function searchCity(event) {
    event.preventDefault();
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${Apikey}&units=metric`;
    let forecastUrl = `https://api.openweathermap.org/data/2.5/forecast/daily?q=${city}&units=metric&cnt=7&appid=${forecastKey}`;
    axios.get(url).then(getData);
    axios.get(forecastUrl).then(getForecast);
    setSubmit(true);

  }



  //console.log(temp);

  return (
    <div className="Search">
      <form onSubmit={searchCity}>
        <input
          type="search"
          placeholder="Type a city..."
          onChange={changeCity}
        ></input>
        <button type="submit" className="btn btn-primary" >Search</button>
      </form>

      <Weather  forecast = {forecast} city={city} submit={submit} temp={temp} desc= {desc} hum = {hum} wind = {wind} emoji = {emoji}/>
    </div>
  );
}
