import React  from  "react";


export default function Forecast(props){


    let src1 = `http://openweathermap.org/img/wn/${props.forecast[0].emoji}@2x.png`
    let src2 = `http://openweathermap.org/img/wn/${props.forecast[1].emoji}@2x.png`
    let src3 = `http://openweathermap.org/img/wn/${props.forecast[2].emoji}@2x.png`
    let src4 = `http://openweathermap.org/img/wn/${props.forecast[3].emoji}@2x.png`
    let src5 = `http://openweathermap.org/img/wn/${props.forecast[4].emoji}@2x.png`



    let days = [
        "Mon",
        "Tue",
        "Wed",
        "Thurs",
        "Fri",
        "Sat",
        "Sun",
      ];




      window.onload = function() {
        getDays();
      }


    let forecastdays = ['day1', 'day2', 'day3' , 'day4', 'day5'];


    function getDays(){



        let Day = loadDate();
        let indexday = days.indexOf(Day)

        forecastdays.map((day) => {
          if (indexday === 6){
            indexday = 0;
            day = days[indexday];

          }else{
            indexday = indexday+1
            day = days[indexday];
          }
        });


      }

      function loadDate() {
        let now = new Date();
        let day = days[now.getDay()];

        return(day);
      }

    return(
    <div className="Forecast">
        <div className="row">
            <div className="col-sm-2">
                <div className="day">{forecastdays[0]}</div>
                <img src={src1} alt="" className="weathericon"></img>
                <div className="dayTemp"> <span>{Math.round(props.forecast[0].temp)}</span>Ëš</div> 
            </div>
            <div className="col-sm-2">
                <div className="day">{props.forecast[1].date}</div>
                <img src={src2} alt="" className="weathericon"></img> 
                <div className="dayTemp"> <span>{Math.round(props.forecast[1].temp)}</span>Ëš</div>

            </div>
            <div className="col-sm-2">
                <div className="day">{props.forecast[2].date}</div>
                <img src={src3} alt="" className="weathericon"></img> 
                <div className="dayTemp"> <span>{Math.round(props.forecast[2].temp)}</span>Ëš</div>

            </div>
            <div className="col-sm-2">
                 <div className="day">{props.forecast[3].date}</div>
                 <img src={src4} alt="" className="weathericon"></img>
                <div className="dayTemp"> <span>{Math.round(props.forecast[3].temp)}</span>Ëš</div> 

            </div>
            <div className="col-sm-2">
                <div className="day">{props.forecast[4].date}</div>
                <img src={src5} alt="" className="weathericon"></img> 
                <div className="dayTemp"> <span>{Math.round(props.forecast[4].temp)}</span>Ëš</div>

            </div>
        </div>

    </div>
    );

}
