import React, {useState} from "react";
import axios from "axios";

export default function CurrentTemp(props){
    console.log(props.temp);

    let [temp, setTemp] = useState(props.temp);
    let [hum, setHum] = useState(null);
    let [wind, setWind] = useState(null);
    let [emoji, setEmoji] = useState(null);

    let Apikey = `3f6be1c407b0d9d1933561808db358ba`;
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${props.city}&appid=${Apikey}&units=metric`;


    if (props.submit === true){
        axios.get(url).then(getData);

    }


    function getData(response) {
      //setTemp(response.data.main.temp);
      setHum(response.data.main.humidity);
      setWind(response.data.wind.speed);
      setEmoji(response.data.weather[0].icon);

    }

    let EmojiSrc = `http://openweathermap.org/img/wn/${emoji}@2x.png`;



    function toFarhenheit(event) {
        event.preventDefault();
        let temperature = Math.round(temp * (9 / 5) + 32);
        setTemp(temperature);
      }

    function toCelcius(event) {
        event.preventDefault();
        let temperature = props.temp;
        setTemp(temperature);

      }


    return(
    <div className="CurrentTemp">


        <div className="row">
            <div className="col-6 mainTemp">
            <div>
                <img className="icon" src={EmojiSrc} alt= {props.desc} />{" "} 

                <span className="Temp">{Math.round(temp)}</span>
                <span className="buttonsCelFar">
                <button className="btn btn-light" id="toCelcius" onClick={toCelcius}>
                {" "}
                °C{" "}
                </button>

                <button className="btn btn-light" id="toFarenheit"  onClick={toFarhenheit}>
                {" "}
                °F{" "}
                 </button>
                </span>

                </div> 
            </div>
            <div className="col-6 HumAndWind" >
                <div className="info">Humidity: {hum} %</div>
                 <div className="info">Wind: {wind} km/h </div>

            </div>
        </div>
    </div>
    );
}
