import React , {useState} from "react";
import { useEffect } from "react";

export default function Temp(props){

    const [temp, setTemp] = useState(props.temp);

    let [submit , setSubmit] = useState(props.submit);

    // console.log(submit);

    MyComponent();

    function MyComponent() {
       useEffect(() => {
          loadData();
       }, []); // Pass an empty array to only call the function once on mount.

       function loadData() {

            setTemp(props.temp);
            console.log(temp);
          // Fetch data or perform other loading logic here
       }

       // ... component render logic
    }

     if ( submit === true && temp !== props.temp){
        setTemp(props.temp);
    } else if (submit === true && temp === props.temp){
        setSubmit(false);
    }



    console.log(temp);


    function toFarhenheit(event) {
      event.preventDefault();
        let temperature = Math.round(temp * (9 / 5) + 32);
        //setSubmit(false);
        setTemp(temperature);
      }

    function toCelcius(event) {
       event.preventDefault();
        let temperature = props.temp;
        setTemp(temperature);

         }

    return(
        <span>
        <span className="Temp">{Math.round(temp)}</span>

        <span className="buttonsCelFar">
        <button className="btn btn-light" id="toCelcius" onClick={toCelcius}>
        {" "}
        °C{" "}
        </button>

        <button className="btn btn-light" id="toFarenheit" onClick={toFarhenheit} >
        {" "}
        °F{" "}
         </button>
        </span>
        </span>

    )
}
