import React, { useState } from "react";
import axios from "axios";

import Weather from "./Weather";

import "./App.css";

export default function Search() {
  let [city, setCity] = useState("London");
  let [submit, setSubmit] = useState(true);

//for Current Temp
  let [desc, setDesc] = useState(null);
  let [temp, setTemp] = useState(null);
  let [hum, setHum] = useState(null);
  let [wind, setWind] = useState(null);
  let [emoji, setEmoji] = useState(null);



  function getData(response) {
    setDesc(response.data.weather[0].description);
    setTemp(response.data.main.temp);
    setHum(response.data.main.humidity);
    setWind(response.data.wind.speed);
    setEmoji(response.data.weather[0].icon);
  }

  function getForecast(response){
    console.log(response);
  }


  function changeCity(event) {
    event.preventDefault();
    setCity(event.target.value);
    setSubmit(false);
  }


  let Apikey = `3f6be1c407b0d9d1933561808db358ba`;



  function searchCity(event) {
    event.preventDefault();
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${Apikey}&units=metric`;
    let forecastUrl = `api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${Apikey}`
    axios.get(url).then(getData);
    axios.get(forecastUrl).then(getForecast);
    setSubmit(true);

  }



  //console.log(temp);

  return (
    <div className="Search">
      <form onSubmit={searchCity}>
        <input
          type="search"
          placeholder="Type a city..."
          onChange={changeCity}
        ></input>
        <button type="submit" className="btn btn-primary" >Search</button>
      </form>

      <Weather city={city} submit={submit} temp={temp} desc= {desc} hum = {hum} wind = {wind} emoji = {emoji}/>
    </div>
  );
}
