export default function Temp(props){

    const [temp, setTemp] = useState(props.temp);

    let [submit , setSubmit] = useState(true);

    // console.log(submit);


     if (( submit === true && temp !== props.temp)){
       toCelcius();
       console.log(0);
     }
    else if (submit === true && temp === props.temp){
        setTemp(props.temp);
        setSubmit(false);
    }



    console.log(temp);
    console.log(submit);
    console.log(props.temp);


    function toFarhenheit(event) {
      event.preventDefault();
        let temperature = Math.round(temp * (9 / 5) + 32);
        //setSubmit(false);
        setTemp(temperature);
      }

    function toCelcius(event) {
       event.preventDefault();
        let temperature = props.temp;
        setTemp(temperature);

         }

    return(
        <span>
        <span className="Temp">{Math.round(temp)}</span>

        <span className="buttonsCelFar">
        <button className="btn btn-light" id="toCelcius" onClick={toCelcius}>
        {" "}
        °C{" "}
        </button>

        <button className="btn btn-light" id="toFarenheit" onClick={toFarhenheit} >
        {" "}
        °F{" "}
         </button>
        </span>
        </span>

    )
}
