import React, { useState } from "react";
import axios from "axios";

import CurrentTemp from "./CurrentTemp";
import Forecast from "./Forecast";

export default function Weather(props) {
  let [desc, setDesc] = useState(null);
  let [temp, setTemp] = useState(null);
  let [hum, setHum] = useState(null);
  let [wind, setWind] = useState(null);
  let [emoji, setEmoji] = useState(null);

  console.log(props.submit);
  console.log(props.city);

  let Apikey = `3f6be1c407b0d9d1933561808db358ba`;
  let url = `https://api.openweathermap.org/data/2.5/weather?q=${props.city}&appid=${Apikey}&units=metric`;

  if (props.submit === true) {
    axios.get(url).then(getData);

  }

  window.onload = function (){
    axios.get(url).then(getData);
  }


  function getData(response) {
    setDesc(response.data.weather[0].description);
    setTemp(response.data.main.temp);
    setHum(response.data.main.humidity);
    setWind(response.data.wind.speed);
    setEmoji(response.data.weather[0].icon);
  }

  if (props.submit === true) {

    return (
      <div className="Weather">
        <h1>{props.city}</h1>

        <div className="info">Thursday 5:45</div>
        <div className="info">{desc}</div>

      <CurrentTemp  city = {props.city} desc= {desc}  temp = {temp} emoji = {emoji} hum = {hum} wind = {wind}/>
      <Forecast city = {props.city}/>
      </div>
    );
  }
}
