import React, {useState} from "react";


import CurrentTemp from "./CurrentTemp";
import Forecast from "./Forecast";

export default function Weather(props) {

    let [date ,setDate] = useState(null);
    let[day , setDay ] = useState(null);

    let days = [
        "Mon",
        "Tue",
        "Wed",
        "Thurs",
        "Fri",
        "Sat",
        "Sun",
      ];


      window.onload = function() {
        getDays();
      }


    function getDays(){
        let day = loadDate();
        let index = days.indexOf(day)


        days.map((day) => {
          if (index === 6){
            index = 0;
            setDay(days[index]);

          }else{
            index = index+1
            setDay(days[index]);
          }
        });


      }

      function loadDate() {
        let now = new Date();
        let day = days[now.getDay()];
        let hours = now.getHours();
        let min = now.getMinutes();
        if (min < 10) {
          min = `0${min}`;
        }
        let time = `${hours}:${min}`;

        setDate  (time);

        return(day);
      }


let EmojiSrc = `http://openweathermap.org/img/wn/${props.emoji}@2x.png`;


    if (props.submit === true || props.submit === "start"){return (
      <div className="Weather">
        <h1>{props.city}</h1>

        <div className="info">{date}</div>
        <div className="info">{props.desc}</div>

      <CurrentTemp submit = {props.submit} city = {props.city} desc= {props.desc}  temp = {props.temp} emoji = {EmojiSrc} hum = {props.hum} wind = {props.wind}/>
      <Forecast forecast = {props.forecast}/>
      </div>
    );
    }
}
