import React, {useState} from "react";

export default function CurrentTemp(props){

    let EmojiSrc = `http://openweathermap.org/img/wn/${props.icon}@2x.png`;


    return(
    <div className="CurrentTemp">


        <div className="row">
            <div className="col-6 mainTemp">
            <div>
                <img className="icon" src={EmojiSrc} alt={props.desc} />{" "} 

                <span className="Temp">{Math.round(props.temp)}</span>
                <span className="buttonsCelFar">
                <button className="btn btn-light" id="toCelcius">
                {" "}
                °C{" "}
                </button>

                <button className="btn btn-light" id="toFarenheit">
                {" "}
                °F{" "}
                 </button>
                </span>

                </div> 
            </div>
            <div className="col-6 HumAndWind" >
                <div className="info">Humidity: {props.hum}</div>
                 <div className="info">Wind: {props.wind}km/h </div>

            </div>
        </div>
    </div>
    );
}
